    int fntableExists(String tableName)
    {
        if (tableName == null)
        {
            return 0;
        }
        int count=0;
        Cursor cursor=null;
        open();
        try {

            cursor = db.rawQuery("SELECT COUNT(*) FROM sqlite_master WHERE type = ? AND name = ?", new String[] {"table", tableName});
            if (cursor.moveToFirst())
            {
                count = cursor.getInt(0);
            }
        }
        catch(Exception e)
        {
            e.printStackTrace();
        }
        finally
        {
            if(cursor!=null) {
                cursor.close();
            }
            close();
        }

        return count;
    }

